mapper.select:
SET @count = NULL; SET @rowNumber = 0; SELECT @count := COUNT(*) AS count FROM nraFile WHERE statusCode > 1 AND isDel = 0 AND auditor IS NOT NULL;
UPDATE nrafile SET queueNo = 0 WHERE statusCode > 1 AND isDel = 0 AND auditor IS NOT NULL;
UPDATE nrafile SET queueNo = @rowNumber := @rowNumber + 1 WHERE id IN( SELECT id FROM ( SELECT * FROM nrafile WHERE queueNo != 0 AND isDel = 0 AND statusCode <= 1 ORDER BY queueNo ) temp ) ORDER BY queueNo;
